import React, { useState } from "react";
import TodoBar from "../components/common/TodoBar";
import { Link } from "react-router-dom";
import { Grid, TextField, Box, Button, Snackbar } from "@material-ui/core";
import MuiAlert from "@material-ui/lab/Alert";
import { makeStyles } from "@material-ui/core/styles";
import axios from "axios";

function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}

const useStyles = makeStyles(theme => ({
  root: {
    width: "100%",
    "& > * + *": {
      marginTop: theme.spacing(2)
    }
  }
}));

function Signup() {
  const classes = useStyles();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [lastname, setLastname] = useState("");
  const [open, setOpen] = useState(false);

  const signup = () => {
    const body = {
      email: email,
      password: password,
      name: name,
      lastname: lastname,
      open: open
    };
    console.log(body);

    axios.post("http://localhost:5000/auth/signup", body).then(
      res => console.log("resss", res.data.flash),
      // res => console.log("data", res.flash),
      // res => console.log("flash", res.data.flash),
      res => setFlash(res.flash),
      err => setFlash(err.flash)
    );
  };

  const handleSubmit = () => {
    setOpen(true);
  };

  const handleClose = (event, reason) => {
    if (reason === "clickaway") {
      return;
    }
    setOpen(false);
  };

  return (
    <div>
      <TodoBar />
      <Grid container justify="center" style={{ height: "100%" }}>
        <Grid item xs={12}>
          <Grid
            container
            alignItems="center"
            justify="center"
            style={{
              display: "flex",
              flexDirection: "column",
              justifyContent: "center",
              padding: "5rem 2rem 2rem 2rem",
              backgroundColor: "#ffedcd"
            }}
          >
            <Box
              justify={"center"}
              border={1}
              borderRadius={15}
              style={{
                padding: "1rem 2rem 3rem 2rem",
                backgroundColor: "white"
              }}
            >
              <h1
                style={{
                  fontFamily: "Poiret One",
                  color: "#0D5FAD",
                  textAlign: "center"
                }}
              >
                Sign up
              </h1>
              <form style={{ textAlign: "center" }}>
                <TextField
                  type="email"
                  name="email"
                  label="email"
                  onChange={e => setEmail(e.target.value)}
                />
                <br />
                <br />
                <TextField
                  type="password"
                  name="password"
                  label="password"
                  onChange={e => setPassword(e.target.value)}
                />
                {/* <br />
                <br />
                <TextField
                  type="password"
                  name="passwordbis"
                  label="confirm password"
                /> */}
                <br />
                <br />
                <TextField
                  type="text"
                  name="name"
                  label="name"
                  onChange={e => setName(e.target.value)}
                />
                <br />
                <br />
                <TextField
                  type="text"
                  name="lastname"
                  label="lastname"
                  onChange={e => setLastname(e.target.value)}
                />
                <br />
                <br />
                {/* <Link to={"/"} type="submit" value="Submit"> */}
                <Button
                  variant="outlined"
                  justify="center"
                  style={{
                    marginTop: "2rem",
                    color: "#0D5FAD",
                    borderColor: "#0D5FAD",
                    fontWeight: "bolder"
                  }}
                  onClick={signup}
                  onChange={handleSubmit}
                >
                  Submit
                </Button>
                {/* </Link> */}
              </form>
              <br />
              <Box style={{ color: "#717D7E", textAlign: "center" }}>
                Already have an account ?
                <br />
                <Link to={"/login"}>
                  <Button>Login</Button>
                </Link>
              </Box>
            </Box>
          </Grid>
        </Grid>
      </Grid>
      <div className={classes.root}>
        <Button variant="outlined" onClick={handleClick}>
          Open success snackbar
        </Button>
        <Snackbar open={open} autoHideDuration={6000} onClose={handleClose}>
          <Alert onClose={handleClose} severity="success">
            This is a success message!
          </Alert>
        </Snackbar>
        <Alert severity="error">This is an error message!</Alert>
        <Alert severity="warning">This is a warning message!</Alert>
        <Alert severity="info">This is an information message!</Alert>
        <Alert severity="success">This is a success message!</Alert>
      </div>
    </div>
  );
}

export default Signup;
