import React, { useState, useEffect } from "react";
import TodoBar from "../components/common/TodoBar";
import TodoList from "../components/TodoList";
import TodoItem from "../components/TodoItem";
import Task from "../components/Task";
import { Link } from "react-router-dom";
import {
  Button,
  Grid,
  Checkbox,
  ListItemText,
  List,
  Box
} from "@material-ui/core/";
import axios from "axios";

function AllTasks() {
  const [all, setAll] = useState([]);
  const [userId, setUserId] = useState(localStorage.getItem("id"));

  const useStyles = makeStyles({
    category: {
      background: catColors,
      border: 0,
      borderRadius: 3,
      boxShadow: "0 3px 5px 2px rgba(255, 105, 135, .3)",
      color: "white",
      pointerEvents: "none"
      // textTransform: "none"
    }
  });
  const classes = useStyles();

  useEffect(() => {
    axios.get(`http://localhost:5000/todo/${userId}/all`).then(
      res => setAll(res.data),
      res => console.log("get res", res.data)
    );
  }, []);
  console.log(all);
  return (
    <div>
      <TodoBar />
      All your Tasks
      <Button component={Link} to={"/"}>
        Go back to Home
      </Button>
      <List>
        <div>
          {all.map(todo => (
            <Task
              task={todo.task}
              date={todo.todo_at}
              category={todo.categories_id}
            />
          ))}
          <Grid
            item
            xs={10}
            md={8}
            lg={5}
            style={{ marginTop: "2rem", alignContent: "center" }}
          >
            <ListItemText
              style={{
                textDecoration: completed ? "line-through" : "none"
              }}
            >
              <Box style={{ display: "flex" }}>
                <Box flexGrow={1}>
                  <Checkbox
                    tabIndex={-1}
                    // checked={completed}
                    onClick={() => toggleTodo(id)}
                  />
                  <Button className={classes.category}>work</Button>
                </Box>
                <Box display="flex" justifyContent="flex-end">
                  <IconButton aria-label="Edit" onClick={toggle}>
                    <EditIcon />
                  </IconButton>
                  <IconButton
                    aria-label="Delete"
                    onClick={() => removeTodo(id)}
                  >
                    <DeleteIcon />
                  </IconButton>
                </Box>
              </Box>
              <div style={{ marginLeft: "0.7rem", width: "80%" }}>
                Wash my car
              </div>
              <Box
                style={{
                  marginLeft: "0.7rem",
                  marginTop: "0.3rem",
                  width: "45%",
                  padding: "0.3rem"
                }}
                border={1}
                borderRadius={10}
              >
                Le Vendredi 5 janvier à 14h30
              </Box>
              {/* <ListItemSecondaryAction> */}
              {/* </ListItemSecondaryAction> */}
            </ListItemText>
          </Grid>
        </div>
      </List>
    </div>
  );
}

export default AllTasks;
