import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Grid, Paper, TextField } from "@material-ui/core";

function Signup() {
  const initialState = [
    {
      email: "enter your email",
      password: "monPassword",
      passwordbis: "monPassword",
      name: "James",
      lastname: "Bond"
    }
  ];
  const [users, setUsers] = useState(initialState);
  const [value, setValue] = useState("");
  const addUser = event => {
    setUsers([
      ...users,
      {
        email: event.target.value,
        password: event.target.value,
        passwordbis: event.target.value,
        name: event.target.value,
        lastname: event.target.value
      }
    ]);
  };

  const handleSubmit = e => {
    e.preventDefault();
    if (!value) return;
    addTodo(value);
    reset();
    toggle();
  };

  return (
    <div>
      <Grid container alignItems="center" style={{ height: "100%" }}>
        <Grid item xs={12}>
          <Paper elevation={4} style={{ margin: "5rem 20rem 0 20rem" }}>
            <Grid
              container
              alignItems="center"
              justify="center"
              style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                padding: "5rem"
              }}
            >
              <h1>Signup</h1>
              <form onSubmit={addUser}>
                <TextField
                  type="email"
                  name="email"
                  label="email"
                  value={value}
                />
                <br />
                <br />
                <TextField
                  type="password"
                  name="password"
                  label="password"
                  value={value}
                />
                <br />
                <br />
                <TextField
                  type="password"
                  name="passwordbis"
                  label="confirm password"
                  value={value}
                />
                <br />
                <br />
                <TextField type="text" name="name" label="name" value={value} />
                <br />
                <br />
                <TextField
                  type="text"
                  name="lastname"
                  label="lastname"
                  value={value}
                />
                <br />
                <br />
                <Link
                  to={"/"}
                  type="submit"
                  value="Submit"
                  onClick={handleSubmit}
                >
                  Submit
                </Link>
              </form>
              <br />
              <div>
                Already an account ? <Link to={"/signin"}> Signin</Link>
              </div>
            </Grid>
          </Paper>
        </Grid>
      </Grid>
    </div>
  );
}

export default Signup;
