import React, { useState, useEffect } from "react";
import TodoBar from "../components/common/TodoBar";
import TodoList from "../components/TodoList";
import TodoItem from "../components/TodoItem";
import Task from "../components/Task";
import { Link } from "react-router-dom";
import {
  Button,
  AppBar,
  Toolbar,
  Typography,
  List,
  Box
} from "@material-ui/core/";
import axios from "axios";

function AllTasks() {
  const [all, setAll] = useState([]);
  const [userId, setUserId] = useState(localStorage.getItem("id"));

  useEffect(() => {
    axios.get(`http://localhost:5000/todo/${userId}/all`).then(
      res => setAll(res.data),
      res => console.log("get res", res.data)
    );
  }, []);
  console.log(all);
  return (
    <div>
      <TodoBar />
      All your Tasks
      <Button component={Link} to={"/"}>
        Go back to Home
      </Button>
      <List>
        <div>
          {all.map(todo => (
            <Task
              task={todo.task}
              date={todo.todo_at}
              category={todo.categories_id}
            />
          ))}
          <Box border={1} borderRadius={10}>
            <h2>wash my car</h2>
            <h4>work</h4>
            <h4>Le Vendredi 5 janvier Ã  14h30</h4>
          </Box>
        </div>
      </List>
    </div>
  );
}

export default AllTasks;
