import React, { useState } from "react";
import { Link } from "react-router-dom";
import { Grid, Paper, TextField } from "@material-ui/core";

function Signup() {
  const initialState = [
    {
      email: "enter your email",
      password: "monPassword",
      passwordbis: "monPassword",
      name: "James",
      lastname: "Bond"
    }
  ];
  const [users, setUsers] = useState(initialState);
  const addUser = event => {
    setUsers([
      ...users,
      {
        email: event.target.value,
        password: event.target.value,
        passwordbis: event.target.value,
        name: event.target.value,
        lastname: event.target.value
      }
    ]);
  };

  return (
    <div>
      <Grid container alignItems="center" style={{ height: "100%" }}>
        <Grid item xs={12}>
          <Paper elevation={4} style={{ margin: "5rem 20rem 0 20rem" }}>
            <Grid
              container
              alignItems="center"
              justify="center"
              style={{
                display: "flex",
                flexDirection: "column",
                justifyContent: "center",
                padding: "5rem"
              }}
            >
              <h1>Signup</h1>
              <form onSubmit={addUser}>
                <TextField type="email" name="email" label="email" />
                <br />
                <br />
                <TextField type="password" name="password" label="password" />
                <br />
                <br />
                <TextField
                  type="password"
                  name="passwordbis"
                  label="confirm password"
                />
                <br />
                <br />
                <TextField type="text" name="name" label="name" />
                <br />
                <br />
                <TextField type="text" name="lastname" label="lastname" />
                <br />
                <br />
                <Link to={"/"} type="submit" value="Submit">
                  Submit
                </Link>
              </form>
              <br />
              <div>
                Already an account ? <Link to={"/signin"}> Signin</Link>
              </div>
            </Grid>
          </Paper>
        </Grid>
      </Grid>
    </div>
  );
}

export default Signup;
